
public class BitwiseOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=5;
		int num2=10;
		System.out.println("Bitwise AND operator "+num1+" & "+num2+" = "+(num1&num2));
		System.out.println("Bitwise OR operator "+num1+" | "+num2+" = "+(num1|num2));
		System.out.println("Bitwise XOR operator "+num1+" ^ "+num2+" = "+(num1^num2));
		System.out.println("Bitwise LEFT SHIFT operator "+num1+" << 1 = "+(num1<<1));
		System.out.println("Bitwise RIGHT SHIFT operator "+num1+" >> 1 = "+(num1>>1));
		System.out.println("Bitwise NOT operator "+" ~ "+num2+" = "+(~num2));

	}

}
