
public class eval_exp18 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5,b=5;
		System.out.println("++a-b-- => "+ (++a-b--)); // 1 a=6 b=4
		System.out.println("a%b++ => "+a%b++); //2  a=6 b= 5
		System.out.println("a*=b+5 => "+ (a*=b+5)); // 60
		System.out.println("x=69>>>2 => "+ (69>>>2)); //17

	}

}
